INSERT INTO infodb.leave_types(type,description)VALUES('Northern Hemisphere winter holidays','Winter in the Northern Hemisphere features many holidays that involve festivals and feasts');
INSERT INTO infodb.leave_types(type,description)VALUES('National holidays','Sovereign nations and territories observe holidays based on events of significance to their history');
INSERT INTO infodb.leave_types(type,description)VALUES('Secular holidays','Several secular holidays are observed such as Earth Day or Arbor Day or Labor Day');
INSERT INTO infodb.leave_types(type,description)VALUES('Unofficial holidays','These are holidays that are not traditionally marked on calendars.');
INSERT INTO infodb.leave_types(type,description)VALUES('Puplic holidays','A public holiday, national holiday or legal holiday is a holiday generally established by law and is usually a non-working day during the year.');
